import React from 'react';
import './App.css';

import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';

import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore'; 

firebase.initializeApp({
    apiKey: "AIzaSyACxBdBKI1MXqALa6OGQgtmYyzlfmRS7TI",
    authDomain: "group-chat-661ae.firebaseapp.com",
    projectId: "group-chat-661ae",
    storageBucket: "group-chat-661ae.appspot.com",
    messagingSenderId: "518769476441",
    appId: "1:518769476441:web:c6800c44f2ca2711b4c248"

})

const auth = firebase.auth();
const firestore = firebase.firestore();


function App() {

  const [user] = useAuthState(auth);

  return (
    <div className="App">
      <header className="App-header">

      </header>

      <section>
        {user ? <ChatRoom /> : <SignIn />}

      </section>


    </div>
  );
}

function SignIn() {

  const signInWithGoogle = () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    auth.signInWithPopup(provider);
  }

  return(
   <button onClick={signInWithGoogle}>Sign in with Google</button>
  )
}

function SignOut() {
return auth.currentUser && (

  <button onClick={() => auth.signOut()}>Sign Out</button>
)
}

function ChatRoom() {

  const messagesRef = firestore.collection('messages');
  const query = messagesRef.orderBy('createdAt').limit(25);

  const [messages] = useCollectionData(query, {idField: 'id'});


  return (
    <>
    <div>
      {messages && messages.map(msg => <GroupMessage key={msg.id} message= {msg} />)}
    </div>

    <div>

    </div>
    </>
  )
}

function  GroupMessage(props) {
  const { text, uid, photoURL } = props.message;

  const messageClass = uid === auth.currentUser.uid ? 'sent' : 'received';

  return (<>
    <div className={`message ${messageClass}`}>
      <img src={photoURL || 'https://api.adorable.io/avatars/23/abott@adorable.png'} />
      <p>{text}</p>
    </div>
  </>)
}

export default App;
